package org.javaweb.vuls.api;

import org.apache.commons.codec.binary.Base64;
import org.javaweb.utils.EncryptUtils;
import org.junit.Test;

public class LogEncrypt {

	private static final String RC4_KEY = "{rc4_key}";

	@Test
	public void decode() {
		String log     = "";
		String content = EncryptUtils.decryptionRC4(Base64.decodeBase64(log), RC4_KEY);

		System.out.println(content);
	}

}
